// Initialize AOS for Animation on Scroll
document.addEventListener('DOMContentLoaded',function(){AOS.init({duration:1000,once:true});});

// Smooth Scroll for Anchor Links
document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();document.querySelector(this.getAttribute('href')).scrollIntoView({behavior:'smooth'});});});

// Form Submission Handling
const form=document.getElementById('contactForm');
form.addEventListener('submit',function(e){e.preventDefault();const formData=new FormData(form);fetch('https://script.google.com/macros/s/AKfycbzKPCYr6NDEHlVQ067ljMSNBPvuDNwO1Ljv0po5DB174Gs5_rdYPIOB5mTqLUWZXuOnEw/exec',{method:'POST',body:formData}).then(response=>{if(!response.ok){throw new Error('Network response was not ok');}return response.json();}).then(data=>{alert('¡Gracias por tu mensaje! Nos pondremos en contacto contigo pronto.');form.reset();}).catch(error=>{console.error('Error:',error);alert('Hubo un problema al enviar el formulario. Inténtalo de nuevo más tarde.');});});

// Cookie Consent
function acceptCookies(){document.getElementById('cookieBanner').style.display='none';localStorage.setItem('cookiesAccepted','true');}
function rejectCookies(){document.getElementById('cookieBanner').style.display='none';localStorage.setItem('cookiesAccepted','false');}
window.addEventListener('load',function(){if(localStorage.getItem('cookiesAccepted')!=='true'){document.getElementById('cookieBanner').style.display='block';}document.getElementById('acceptButton').addEventListener('click',acceptCookies);document.getElementById('rejectButton').addEventListener('click',rejectCookies);});
